const _ = require('lodash')

let foods =
  {name: ['chicken nuggets', 'big mac', 'ceaser salad', 'french fries', 'yogurt', 'cereal', 'potato chips', 'shiro', 'coffee', 'apple', 'orange', 'olives', 'banana', 'bell pepper', 'taco', 'salsa', 'guacamole', 'cheddar', 'baguette', 'cashews', 'veggie burger', 'sweet burrito', 'pickles', 'salmon', 'skittles', 'carrot', 'cucumber', 'cream cheese', 'bagel', 'meatloaf'],
  type: ['breakfast', 'lunch', 'dinner', 'snack'],
  healthy: [true, false],
  calories: [100, 150, 200, 250, 300, 350, 400, 450, 500, 550, 600],
  temp: ['cold', 'room temp', 'hot'],
  color: ['white', 'brown', 'green', 'yellow', 'red', 'black', '']}

function getRandom(string){
   let request = _.sample(foods[string])
   return {request}
}

module.exports = getRandom
